,p_process_sql_clob=>wwv_flow_utilities.join(wwv_flow_t_varchar2(
'--supprimer les existants si pas dans le shuttle',
'delete vd_afw_19_dispn_fonct df',
' where instr ('':'' || :P1030_DISPN_FONCT || '':'', '':'' || df.ref_fonct || '':'') = 0 ',
'   and df.ref_prodt = :A_AFW_04_SOURC_SEQNC_CONTX',
'   and exists (select null',
'                 from vd_afw_19_dispn_fonct',
'                where ref_prodt = :A_AFW_04_SOURC_SEQNC_CONTX',
'                  and seqnc = df.seqnc);',
'',
'',
'--insérer les non existants',
'insert into vd_afw_19_dispn_fonct',
'            (ref_prodt,',
'             ref_fonct,',
'             valr_atrib_1,',
'             valr_atrib_2,',
'             valr_atrib_3,',
'             valr_atrib_4,',
'             valr_atrib_5)',
'  select :A_AFW_04_SOURC_SEQNC_CONTX,',
'         seqnc,',
'         case when f.titre_atrib1 is null then null else f.valr_deft_atrib1 end,',
'         case when f.titre_atrib2 is null then null else f.valr_deft_atrib2 end,',
'         case when f.titre_atrib3 is null then null else f.valr_deft_atrib3 end,',
'         case when f.titre_atrib4 is null then null else f.valr_deft_atrib4 end,',
'         case when f.titre_atrib5 is null then null else f.valr_deft_atrib5 end',
'    from vd_afw_19_fonct f',
'   where instr ('':'' || :P1030_DISPN_FONCT || '':'', '':'' || f.seqnc || '':'') > 0',
'     and not exists (select null',
'                       from vd_afw_19_dispn_fonct',
'                      where ref_prodt = :A_AFW_04_SOURC_SEQNC_CONTX and ref_fonct = f.seqnc);',
'',
'--générer les clés                    ',
'merge into vd_afw_19_cle dest',
'using (select df.seqnc ,',
'              l.seqnc ref_lang,',
'              decode(d.lvl,1,''O'',2,''N'') indic_inter',
'         from vd_afw_19_dispn_fonct df,',
'              vd_afw_19_fonct f,',
'              (select l.seqnc',
'                 from vd_afw_01_lang l',
'                where (exists (select null',
'                                 from vd_afw_11_prodt p',
'                                where p.seqnc = :A_AFW_04_SOURC_SEQNC_CONTX',
'                                  and p.ref_lang = l.seqnc)',
'                       or ',
'                       exists (select null',
'                                 from vd_afw_11_prodt p,',
'                                      vd_afw_11_prodt_lang pl                       ',
'                                where p.seqnc = :A_AFW_04_SOURC_SEQNC_CONTX',
'                                  and pl.ref_prodt = p.seqnc',
'                                  and pl.ref_lang = l.seqnc)',
'                      )',
'               ) l,',
'               (select level lvl',
'                  from dual',
'                connect by level <= 2) d',
'        where df.ref_prodt = :A_AFW_04_SOURC_SEQNC_CONTX',
'          and f.seqnc = df.ref_fonct',
'          and f.indic_cle = ''O''',
'      ) sourc',
'  on (sourc.seqnc = dest.ref_dispn_fonct and sourc.ref_lang = dest.ref_lang)',
'    when matched then',
'      update set seqnc = sourc.seqnc ',
'      where 1=2',
'    when not matched then',
'      insert (ref_dispn_fonct,',
'              ref_lang,',
'              indic_inter)',
'      values(sourc.seqnc,',
'             sourc.ref_lang,',
'             sourc.indic_inter);',
'             ',
'--générer les boutons',
'merge into vd_afw_19_boutn dest',
'using (select df.seqnc ,',
'              l.seqnc ref_lang,',
'              f.code',
'         from vd_afw_19_dispn_fonct df,',
'              vd_afw_19_fonct f,',
'              (select l.seqnc',
'                 from vd_afw_01_lang l',
'                where (exists (select null',
'                                 from vd_afw_11_prodt p',
'                                where p.seqnc = :A_AFW_04_SOURC_SEQNC_CONTX',
'                                  and p.ref_lang = l.seqnc)',
'                       or ',
'                       exists (select null',
'                                 from vd_afw_11_prodt p,',
'                                      vd_afw_11_prodt_lang pl',
'                                where p.seqnc = :A_AFW_04_SOURC_SEQNC_CONTX',
'                                  and pl.ref_prodt = p.seqnc',
'                                  and pl.ref_lang = l.seqnc)',
'                      )',
'               ) l',
'        where df.ref_prodt = :A_AFW_04_SOURC_SEQNC_CONTX',
'          and f.seqnc = df.ref_fonct',
'          and f.indic_boutn = ''O''',
'      ) sourc',
'  on (sourc.seqnc = dest.ref_dispn_fonct and sourc.ref_lang = dest.ref_lang)',
'    when matched then',
'      update set seqnc = sourc.seqnc ',
'      where 1=2',
'    when not matched then',
'      insert (ref_dispn_fonct,',
'              ref_lang,',
'              item_subst,',
'              templ_boutn,',
